void Layout(Visual parent)
{
    for (var i = 0; i < 4; i++)
    {
        var btn = new Button();
        btn.Margin = 5;
        btn.FocusedChanged += (s, e) =>
        {
            btn.Text.Text = e.Value ? "Focused" : "Unfocused";
        };
        btn.Text.Text = "Unfocused";
        parent.Children.Add(btn);

        // focus the first one
        if (i == 0)
        {
            btn.Focus();
        }
    }
}

class Program
{
    static void Main()
    {
        // create application
        using (var dw = new Application())
        {
            // add (main) window to application
            var win = new Window { Title = "Wice" };
            win.ResizeClient(400, 400);
            win.Center();
            win.Show();
            dw.Run();
        }
    }
}

void Layout(Visual parent)
{
    var btn = new Button();
    btn.Click += (s, e) =>
    {
        var window = new Window();

        // we draw our own titlebar using Wice
        window.WindowsFrameMode = WindowsFrameMode.None;
        window.Center();
        window.Show();

        // add a Wice titlebar
        var titleBar = new TitleBar { IsMain = true };
        window.Children.Add(titleBar);
        window.ResizeClient(400, 400);
    };

    btn.Text.Text = "Open new window";
    parent.Children.Add(btn);
}

void Layout(Visual parent)
{
    var btn = new Button();
    btn.Text.Text = "Hover me!";
    parent.Children.Add(btn);

    // a tooltip content is created using a function
    btn.ToolTipContentCreator = tt => createToolTipContent(tt, "hello world");

    // here is a custom tooltip creator function
    void createToolTipContent(ToolTip tt, string text)
    {
        // clear default tooltip shadow
        tt.Content.RenderShadow = null;

        // note we must use the tooltip's compositor, not our window's compositor
        // as the tooltip has its own window
        tt.Content.RenderBrush = tt.Compositor.CreateColorBrush(_D3DCOLORVALUE.Red);

        var tb = new TextBox();
        tb.Margin = 4;
        tb.Text = text;
        tb.ForegroundBrush = new SolidColorBrush(_D3DCOLORVALUE.White);
        tb.FontSize = 25;
        tt.Content.Children.Add(tb);
    }
}

void Layout(Visual parent)
{
    var btn = new Button();
    btn.Text.Text = "Hover me!";
    parent.Children.Add(btn);

    // a tooltip content is created using a function
    // Wice provides a default function for default tooltips
    btn.ToolTipContentCreator = tt => Window.CreateDefaultToolTipContent(tt, "hello world");
}

void Layout(Visual parent)
{
    var btn = new Button();
    btn.Text.Text = "Open the VisualsTree Window";
    btn.Click += (s, e) =>
    {
        // note with .NET Framework you must reference Windows Forms to use the VisualsTree window
        var vt = new Utilities.VisualsTree();
        vt.SetCurrentWindow(parent.Window);
        vt.Show(NativeWindow.FromHandle(parent.Window.Handle));
    };

    parent.Children.Add(btn);
}
