}

void Layout(Visual parent)
{
    // stack state button and textbox
    var stack = new Stack { Orientation = Orientation.Horizontal };
    parent.Children.Add(stack);

    // add state button
    var btn = new StateButton { RenderBrush = Compositor!.CreateColorBrush(D3DCOLORVALUE.Blue.ToColor()) };

    // add "true" / checked state
    btn.AddState(new StateButtonState(true, (button, args, state) => new TextBox
    {
        ForegroundBrush = new SolidColorBrush(D3DCOLORVALUE.White),
        Text = "X",
        VerticalAlignment = Alignment.Center,
        HorizontalAlignment = Alignment.Center
    })); ;

    // add "false" / unchecked set
    btn.AddState(new StateButtonState(false, (button, args, state) => new TextBox()));
    btn.Width = parent.Window!.DipsToPixels(20);
    btn.Height = parent.Window!.DipsToPixels(20);

    // add results textbox
    var results = new TextBox
    {
        Margin = D2D_RECT_F.Thickness(parent.Window!.DipsToPixels(10), 0),
        HorizontalAlignment = Alignment.Center,
        VerticalAlignment = Alignment.Center
    };
    stack.Children.Add(results);

    stack.Children.Add(btn);
    btn.Click += (s, e) => { results.Text = "\"CheckBox\" StateButton is " + (((bool)btn.Value!) ? "Checked" : "Unchecked"); };
}

void Layout(Visual parent)
{
    // stack state button and textbox
    var stack = new Stack { Orientation = Orientation.Horizontal };
    parent.Children.Add(stack);

    // add state button
    var btn = new StateButton
    {
        AutoSize = false,
        Width = parent.Window!.DipsToPixels(100),
        Height = parent.Window!.DipsToPixels(100)
    };
    for (var i = 0; i < 4; i++)
    {
        // second parameter defines the child visual, here just a textbox
        btn.AddState(new StateButtonState("state " + i, (button, args, state) =>
        {
            var tb = new TextBox
            {
                Text = state.ToString(),
                HorizontalAlignment = Alignment.Center,
                VerticalAlignment = Alignment.Center
            };
            return tb;
        }));
    }

    // select one value
    btn.Value = "state 0";
    btn.RenderBrush = Compositor!.CreateColorBrush(D3DCOLORVALUE.OldLace.ToColor());
    stack.Children.Add(btn);

    // add results textbox
    var results = new TextBox
    {
        Margin = D2D_RECT_F.Thickness(parent.Window!.DipsToPixels(10), 0),
        HorizontalAlignment = Alignment.Center,
        VerticalAlignment = Alignment.Center
    };
    stack.Children.Add(results);

    btn.Click += (s, e) => { results.Text = "StateButton value is " + btn.Value; };
}

void Layout(Visual parent)
{
    // dock toggle switches and textbox
    var dock = new Dock();
    parent.Children.Add(dock);

    // add toggle switches
    var list = new List<ToggleSwitch>();
    for (var i = 0; i < 3; i++)
    {
        var toggle = new ToggleSwitch
        {
            Margin = D2D_RECT_F.Thickness(parent.Window!.DipsToPixels(10), 0),
            Name = "switch " + i
        };
        dock.Children.Add(toggle);
        list.Add(toggle);
    }

    // add results textbox
    var results = new TextBox
    {
        Margin = D2D_RECT_F.Thickness(parent.Window!.DipsToPixels(10), 0)
    };
    dock.Children.Add(results);

    // hook click events
    list.ForEach(t => t.Click += (s, e) =>
    {
        var rb = (ToggleSwitch)s!;
        results.Text = "Selected ToggleSwitch is " + rb.Name;

        // use a method extension to deselect all other buttons
        list.Select(rb);
    });
}

