void Layout(Visual parent)
{
    var canvas = new Canvas
    {
        Width = 120,
        Height = 120,
        RenderBrush = Compositor!.CreateColorBrush(D3DCOLORVALUE.Gray.ToColor())
    };
    parent.Children.Add(canvas);

