void Layout(Visual parent)
{
    var btn = new Button();
    btn.Text.Text = "Open a new Window...";
    btn.Click += (s, e) =>
    {
        var window = new Window { Title = "Hello World", CreateOnCursorMonitor = true };
        window.RenderBrush = window.Compositor!.CreateColorBrush(D3DCOLORVALUE.Green.ToColor());
        window.ResizeClient(
            parent.Window!.DipsToPixels(400),
            parent.Window.DipsToPixels(400)
        );
        window.Center();
        window.Show();

        var close = new Button();
        close.Text.Text = "Close me";
        close.Click += (_, __) => { window.Destroy(); };

        window.Children.Add(close);
    };

    parent.Children.Add(btn);
}
